class Hymn:
    def __init__(self, arr:list):
        if len(arr) > 0 and arr[0] != '':
            self.theme = arr[0].replace(" ","")
            self.number = arr[1].replace(" ","")
            self.name = arr[2]
            self.key = arr[3].replace(" ","")
        else:
            self.theme = ""
            self.number = ""
            self.name = ""
            self.key = ""
            
    def __is_contain__(self, value:str, param_type:int):
        '''
        Hymn class의 특정 type이 value와 같은지 여부를 return 합니다.
        '''
        if value == "":
            return False
        if param_type == 0:
            return self.theme == value
        if param_type == 1:
            return self.number == value
        if param_type == 2:
            return self.name == value
        if param_type == 3:
            return self.key == value
        
        
